    def getTestPinsList(self):
        for x in range(1,13):
            if eval("self.PinX1_{}Select.GetSelection()".format(x))!=2:
                exec("self.test.TestPinsList.addPin({}, self.PinX1_{}Select.GetSelection(), 0, self.PinX1_{}Description.GetValue())".format(x+53, x, x))
